import React from 'react';
import nirvana from '../images/nirvana.jpg'
import chopin from '../images/chopin.jpg'
import '../styles/Canciones.css'

class Canciones extends React.Component{
    render (){
        return (
            <div className="alert" id="Cancion">
                <img src={nirvana} id="Cancion__img" alt=""/>    
                <p className="d-inline-block " id="Cancion__texto">Smells like a teen spirit </p>
                <p className="d-inline-block " id="Autor__texto">Nirvana</p>
                <p className="d-inline-block " id="Duracion__texto">5 minutos </p>
                
            </div>
/
            <div className="alert" id="Cancion1">
            <img src={chopin} id="Cancion__img" alt=""/>    
            <p className="d-inline-block " id="Cancion__texto">Op Nocturne n 9 </p>
            <p className="d-inline-block " id="Autor__texto">Fred√©ric Chopin</p>
            <p className="d-inline-block " id="Duracion__texto">5 minutos </p>
            
            </div>
        )
    }
}
export default Canciones;
