class Header extends React.Component{
    render (){
        return(
         <section className="banner">
             <div className="container">
    <div className="banner__hero">
        <div className="banner__img-hero">
            <img src="./img/bandejapaisa.PNG"  width="auto" height= "auto" alt=""/>
        </div>
        <div className="banner__text-hero">
            <h2>"No hay nada mejor que una bandejita pa' calmar el filo"</h2>
        </div> 
    </div>

            </div>
         </section>
        )
    }
}
export default Header;
